import React, { useState } from 'react';

import './landingPage.scss';

const LandingPage = () => {
  const [loading, isLoading] = useState(false);
  return (
    <div>
      <p>
        Basalt is a full-service digital agency that believes design systems empower businesses to
        grow and meet their unique goals. We help large, enterprise organizations increase speed to
        market, decrease their in-house technical teams’ burdens, and ensure brand consistency and
        information accuracy through design systems and other digital solutions. Our objective is to
        define and map an organization’s long-term vision, and then create the tools needed to reach
        those goals.
      </p>
    </div>
  );
};

export default LandingPage;
